!function(a){
	a.fn.lazylay =function(){
		return this.each(function() {
			var t = a(this), 
 			dImg=t.attr('data-image'),
			iWid=Math.round(t.width()),
            iHei=Math.round(t.height()),
            iSiz='w'+iWid+'-h'+iHei+'-p-k-no-nu',
            img='';
			if(dImg.match('/s72-c')){
          		img=dImg.replace('/s72-c','/'+iSiz);
            } else if(dImg.match('/w72-h')){
              	img=dImg.replace('/w72-h72-p-k-no-nu','/'+iSiz);
            } else if(dImg.match('=w72-h')){
              	img=dImg.replace('=w72-h72-p-k-no-nu','='+iSiz);
            } else{
              	img=dImg;
            }		
			a(window).on('load resize scroll',lazyload);
			function lazyload(){
				var wHeight = a(window).height(),scrTop=a(window).scrollTop(),offTop=t.offset().top;
				if(wHeight+scrTop>offTop){
					var i=new Image();
					i.onload=function(){
						t.attr('style','background-image:url('+this.src+')').addClass('lazy-lay');
					},i.src=img;
				}
			}
 			lazyload();
		})
	}
}(jQuery);
