function l(feed,i){
	for (var k = 0; k < feed[i].link.length; k++) {
 		if (feed[i].link[k].rel == 'alternate') {
           link = feed[i].link[k].href;
           break;
        }                           
	}
 	return link;
}

function t(feed,i){
	//var title = feed[i].title.$t;
	return feed[i].title.$t;
	//return title;
}
function e(feed, i) {
	var d = feed[i].published.$t, y = d.substring(0, 4), m = d.substring(5, 7), a = d.substring(8, 10);	
	var h = monthFormat[parseInt(m, 10) - 1] + "/" + a + ", " + y;
    //var code = '<span class="post-date">'+ monthFormat[parseInt(m, 10) - 1] + "/" + a + ", " + y +'</span>';
	return '<span class="post-date">'+ monthFormat[parseInt(m, 10) - 1] + "/" + a + ", " + y +'</span>';

}

function p(feed, i) { 
	if (feed[i].category != undefined) {
		var tag = feed[i].category[0].term;
		code = '<span class="post-tag">' + tag + '</span>';
	} else {
		code = '';
	}
	return code;	
}

function r(feed, i) {
	//var n = feed[i].author[0].name.$t;
    //code = '<span class="author-rc">' + n + '</span>';    
   // return code;
	return feed[i].author[0].name.$t;
}


function g(feed,i,title) {
	var recenthumb;
    try {
       recenthumb = feed.media$thumbnail.url;
       // entry.media$thumbnail.replace(/\/s72\-c\//,'/s400/');
    } catch (error) {
       s = feed[i].content.$t;
       a = s.indexOf("<img");
       b = s.indexOf("src=\"", a);
       c = s.indexOf("\"", b + 5);
       d = s.substr(b + 5, c - b - 5);
       if ((a != -1) && (b != -1) && (c != -1) && (d != "")) {
           recenthumb = d;
       } else recenthumb = 'https://blogger.googleusercontent.com/img/b/R29vZ2xl/AVvXsEhynJiYftRS0DyGobgz4FRXmY2ji6UA7NO90TNWp3zim-2fxQj6kMT2Xn14VSgGTXfDtu34sfVcrBm9lG-V45g_fhoTCUBnxr_VxtdC2JhdSRP-tVOcqgsyv5u6EG9-GyLpLfYNbVkQKAEB/s1600/no-thumb.png';
       }
	var postImageUrl = recenthumb;
	var posterImage = postImageUrl ? '<a class="post-image-link" href="'+ link +'"><img class="post-thumb" src="' + postImageUrl + '" alt="' + title + '"></a>' : ''; 	

	//console.log(posterImage);
	return posterImage;
}

function Posts($this, type, count, label) {
	console.log($this, type, count, label);
    if ( type.match('news') || type.match('comments') || type.match('list') ||type.match('related')) {
		var url = '';
    	if (label == 'recent') {
           url = '/feeds/posts/default?alt=json-in-script&max-results=' + count;
        } else if (label == 'comments') {
           url = '/feeds/comments/default?alt=json&max-results=' + count;
		} else {
           url = '/feeds/posts/default/-/' + label + '?alt=json-in-script&max-results=' + count;
        }
        $.ajax({
                url: url,
                type: 'get',
                dataType: 'jsonp',
                success: function(json) {
					if (type.match('news')) {  
                        var code = '<ul class="my-news-ticker">';
					} else if (type.match('comments')) {  
                        var code = '<ul class="comments-list">';
                    }  else if (type.match('list')) {
						var code = '<ul class="custom-widget">';
                    } else if (type.match('related')) {
                        var code = '<ul class="related-posts">';
                    }
					var entry = json.feed.entry;   console.log(entry);
                    if (entry != undefined) {
						for (var i = 0,feed = entry;i < entry.length; i++) {
							var link = l(feed,i);
                            //console.log(link);
                            var title = t(feed,i);
                            //console.log(title);
                            var date = e(feed, i);
                            //console.log(date);
                            var	image = g(feed,i,title);
                            //console.log(image);
							var tag = p(feed, i);
							//console.log(tag);
							var author = r(feed, i); 

							var content = '';

							if (type.match('news')) {                           
								content += '<li><a href="' + link + '">' + title +'</a></li>';
							} else if (type.match('list')) {                           
								content += '<li class="recent-item">' + image + '<div class="post-info"><h3><a href="' + link + '">' + title +'</a></h3>'+ date +'</div></li>';
                            } else if (type.match('related')) {
                                content += '<li class="related-item">' + image + '<div class="post-info"><h3><a href="' + link + '">' + title + '</a></h3></div></li>';
                           }  else if (type.match('show')) {
                                content += '<div class="comments-item"><div class="comments-image"><img src="//www.blogger.com/img/blogger_logo_round_35.png" alt=""></div><div class="comments-info"><a href="' + link + '">'+ author +'</a><p>' + title +'</p></div></div>';
						   } 

                           code += content;                           
						}
                        code += '</ul>';     	
                    } else {
                        code = '<ul class="no-posts">No Posts Found</ul>';
                    }
                    $this.html(code);
			  }
        });
    }
}


