$(".Label a").attr("href",function($this,href) {
	return href.replace(href,href + '?&max-results=' + postPerPage);
})


$('.post-body strike').each(function() { //left-sidebar add strike
	var $this = $(this);
	var type = $this.text();
	if (type.match('left-sidebar')) {
          $this.replaceWith("<style>.item #content-wrapper { flex-direction: row-reverse}</style>");
    }
    if (type.match('right-sidebar')) {
         $this.replaceWith("<style>.item #content-wrapper { flex-direction: row}</style>");
    }
    if (type.match('full-width')) {
         $this.replaceWith("<style>.item #main-wrapper{width:100%} .item #sidebar-wrapper{display:none}</style>");
    }
})

$(".post-body a").each(function() {  // if a.href is button
	var t = $(this)
	var e = t.text().trim()
	var a = e.split("/"),s = a[0], i = a[1], r = a.pop();
 	e.match("button") && (t.addClass("button").text(s), "button" != i && t.addClass(i), "button" != r && t.addClass("colored-button").css({ "background-color": r }))
})

$(".post-body blockquote").each(function() {
	var e=$(this),
        t=e.text().toLowerCase().trim(),
        h=e.html();
    if(t.match("(alert-success)")){
    	const t = h.replace("(alert-success)","");
        e.replaceWith('<div class="alert-message alert-success">'+ t +"</div>")
    }
	if(t.match("(alert-info)")){
    	const t = h.replace("(alert-success)","");
        e.replaceWith('<div class="alert-message alert-info">'+ t +"</div>")
    }
	if(t.match("(alert-warning)")){
    	const t = h.replace("(alert-success)","");
        e.replaceWith('<div class="alert-message alert-warning">'+ t +"</div>")
    }
	if(t.match("(alert-error)")){
    	const t = h.replace("(alert-success)","");
        e.replaceWith('<div class="alert-message alert-error">'+ t +"</div>")
    }
	if(t.match("(codebox)")){
    	const t = h.replace("(codebox)","");
        e.replaceWith('<pre class="code-box">' + t + "</pre>")
    }

	if(t.match("(contact-form)")){
    	//const t = h.replace("(contact-form)","");
        e.replaceWith('<div class="contact-form"/>');	
    }$(".contact-form").append($("#ContactForm1"))
	/*
	t.match("contact-form") && (e.replaceWith('<div class="contact-form"/>'), 
    $(".contact-form").append($("#ContactForm1")))*/

})


$(".post-body b").each(function() {
	var e = $(this),
		t = e.text(),
		a = t.toLowerCase().trim();  console.log(t,a);
		if (a.match('catalog')) {
			e.replaceWith('<div class="anchor-wrap"><div class="anchor-top"><span class="anchor-title">' + t + '</span><a href="javascript:;" class="tocify-title" role="button" title="' + t + '"><span class="show"></span></a></div><div class="anchor-inner"><ol id="anchor"></ol></div></div>')
    	}
		$(".tocify-title").each(function(e) {
            (e = $(this)).on("click", function() {
                e.toggleClass("is-expanded"), $("#anchor").slideToggle(500)
            })
        }), 
        $("#anchor").toc({
            content: ".post-body",
            headings: "h2,h3,h4"
        }),
		$("#anchor li a").each(function(e) {
            (e = $(this)).click(function() {
                return $("html,body").animate({
                    scrollTop: ($(e.attr("href")).offset().top) - 70
                }, 1000)
            })
		})
})


