$('#related-ready').each(function() {
	var $this = $(this);
 	label = $this.find('.related-tag').data('label');
	Posts($this, 'related', 3, label); 
});


function Posts($this, type, count, label) {
	console.log($this, type, count, label);
    if (type.match('list') ||type.match('related')) {
		var url = '';
    	if (label == 'recent') {
           url = '/feeds/posts/default?alt=json-in-script&max-results=' + count;
        } else {
           url = '/feeds/posts/default/-/' + label + '?alt=json-in-script&max-results=' + count;
        }

        $.ajax({
                url: url,
                type: 'get',
                dataType: 'jsonp',
				beforeSend:function() {
 					if (type.match('list')) {
						$this.html('<div class="loader"/>').parent().addClass('show-slider');
                    }
				},
                success: function(json) {
					if (type.match('list')) {
                        var code = '<ul id="slider-wrap" class="owl-carousel owl-theme">';
                    } else if (type.match('related')) {
                        var code = '<ul class="related-posts">';
                    }
					var entry = json.feed.entry;
                    if (entry != undefined) {
						for (var i = 0,feed = entry;i < entry.length; i++) {
							var link = p_link(feed,i);
                            //console.log(link);
                            var title = p_title(feed,i);
                            //console.log(title);
                            var date = p_date(feed, i);
                            //console.log(date);
                            var	image = p_Image(feed,i,title);
                            //console.log(image);
							var tag = p_label(feed, i);
							//console.log(tag);

							var content = '';
							if (type.match('list')) {                              
								content += '<div class="slider-item">' + image + '<div class="post-info"><h3><a href="' + link + '">' + title +'</a></h3>'+ date +'</div></div>';

                            } else if (type.match('related')) {
                                content += '<li class="related-item">' + image + '<div class="post-info"><h3><a href="' + link + '">' + title + '</a></h3></div></li>';
                            }
                            code += content;                         
						}
                    	code += '</ul>';
                    } else {
                        code = '<ul class="no-posts">Error: No Posts Found</ul>';
                    } if (type.match('list')) {
                        $this.html(code).parent().addClass('show-slider');			
						$("#slider-wrap").owlCarousel({
                            loop: true, // 循環輪播
                            margin:15, // 與右邊圖片的距離
                            nav: true, // 導航文字
                            dots:false,

                            autoplay: false, // 自動輪播
                            autoplayTimeout: 1000, // 切換時間
                            autoplayHoverPause: true, // 滑鼠經過時暫停
                            responsive: {
                              0: {
                                  items: 1 // 螢幕大小為 0~600 顯示 1 個項目
                              },
                              860: {
                                  items: 2 // 螢幕大小為 600~1000 顯示 3 個項目
                              }
                            }
                        })

                    } else {
                        $this.html(code);
                    }
			  }
        });
    }
}


function p_link(feed,i){
	for (var k = 0; k < feed[i].link.length; k++) {
 		if (feed[i].link[k].rel == 'alternate') {
           link = feed[i].link[k].href;
           break;
        }                           
	}
 	return link;
}

function p_title(feed,i){
	var title = feed[i].title.$t;
	return title;
}
function p_date(feed, i) {
	var d = feed[i].published.$t, y = d.substring(0, 4),m = d.substring(5, 7),a = d.substring(8, 10);	
	var h = y + "/" + m + "/"  + a ;
    var code = '<span class="post-date">'+ h +'</span>';
	return code;
}

function p_label(feed, i) { 
	if (feed[i].category != undefined) {
		var tag = feed[i].category[0].term;
		code = '<span class="post-tag">' + tag + '</span>';
	} else {
		code = '';
	}
	return code;	
}
function p_Image(feed,i,title) {
	var recenthumb;
    try {
       recenthumb = feed.media$thumbnail.url;
       // entry.media$thumbnail.replace(/\/s72\-c\//,'/s400/');
    } catch (error) {
       s = feed[i].content.$t;
       a = s.indexOf("<img");
       b = s.indexOf("src=\"", a);
       c = s.indexOf("\"", b + 5);
       d = s.substr(b + 5, c - b - 5);
       if ((a != -1) && (b != -1) && (c != -1) && (d != "")) {
           recenthumb = d;
       } else recenthumb = 'https://blogger.googleusercontent.com/img/b/R29vZ2xl/AVvXsEgZpxOKrgvKr91vlO3rt9bTXd64r9G2DAb-0hXtlakPFgWUBo5MHn4yuw6wN2jyD5lY7ibcQZL5P1RbkNDKIZq3wU5Iqp8Kyw978SxeaDvHEOdpq0TZQWPRQd3g9Ae7bOFUwBvQVJUtRX7_/s1600/no-thumb.png';
       }

    var postImageUrl = recenthumb;
	var posterImage = postImageUrl ? '<a class="post-image-link" href="'+ link +'"><img class="post-thumb" src="' + postImageUrl + '" alt="' + title + '"></a>' : '';
	//console.log(posterImage)
	return posterImage;
}


$(".tab-container:first").show();
$(".tab li:first").addClass('active');

$(".tab li").click(function(){
	index = $(this).index();
    $(".tab li").removeClass('active');
    $(this).addClass('active');
    $(".tab-container").hide();
    $(".tab-container").eq(index).show();
});

