});
$('#top li a').on('click', function() {
	var link_id = $(this).attr('href');            
	var $anchor =  $(link_id);            
	if ($anchor.length) {            
		$('html, body').animate({
           scrollTop: $anchor.offset().top - 70
        }, 1000);
        return false;				
    }
});		

