        console.log(t,a, type,label)
      if (type.match('megatabs')) {
          t.attr('href', '/search/' );
      }
      else if (type.match('megamenu')) {
        if (label.match('recent')) {
          t.attr('href', '/search/' );
        } else {
          t.attr('href', '/search/label/' + label + '?&max-results=200');
        }
      }
});

